'use strict';

var promisify = require('promisify-es6');

var pull = require('pull-stream');

module.exports = function (self) {
  return promisify(function (ipfsPath, options, callback) {
    if (typeof options === 'function') {
      callback = options;
      options = {};
    }

    options = options || {};
    pull(self.refsPullStream(ipfsPath, options), pull.collect(function (err, values) {
      if (err) {
        return callback(err);
      }

      callback(null, values);
    }));
  });
}; // Preset format strings


module.exports.Format = {
  default: '<dst>',
  edges: '<src> -> <dst>'
};
