import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Web3 from 'web3';
import TruffleContract from 'truffle-contract';
import UnlockMetamask from "./Components/UnlockMetamask";
import Medical from './build/contracts/Medical.json';
import SetSick from "./build/contracts/SetSick.json";
import SickFactory from './build/contracts/SickFactory.json';
import Container from './Components/Container';
import ReactDOM from 'react-dom';
import Header from './Components/Header';
import 'react-sticky-header/styles.css';
import SideBar from './Components/SideBar';
import Content from './Components/Content';


class App extends Component {
  constructor() {
    super();
    this.isWeb3 = true;
    this.isWeb3Locked = false;
    this.appName = "Patient Data";

    this.state = {
      network: "checking...",
      accounts: null,
      medicalContract:null,
      patientData:[],
      sickCodes:[
        {
          idPatient: 1000,
          data:[]

        },
        {
          idPatient: 999,
          data:[]

        }
      ],
      sickCodeByPatient:[]

    };

    let web3 = window.web3;
    if (typeof web3 !== "undefined") {
      this.web3Provider = web3.currentProvider;
      this.web3 = new Web3(web3.currentProvider);
      this.medicalContract = TruffleContract(Medical);
      this.medicalContract.setProvider(this.web3Provider);

      if (web3.eth.coinbase === null)
        this.isWeb3Locked = true;
    } else {

      this.isWeb3 = false;
    }
  }

  setNetwork = () => {
    let networkName,
      that = this;

    this.web3.version.getNetwork(function(err, networkId) {
      switch (networkId) {
      case "1":
        networkName = "Main";
        break;
      case "2":
        networkName = "Morden";
        break;
      case "3":
        networkName = "Ropsten";
        break;
      case "4":
        networkName = "Rinkeby";
        break;
      case "42":
        networkName = "Kovan";
        break;
      default:
        networkName = networkId;
      }
      that.setState({
        network: networkName
      })
    });
  };

  getPatient = ()=>{
    }


  componentDidMount = async() => {
    let account = this.web3.eth.coinbase;
    let app = this;
try{
    this.setNetwork(); 

    const accounts =this.web3.eth.accounts[0];
    // const PatientDeploy= PatientFactory.networks[this.state.network];
    // const patientContract =this.web3.eth.contract(
    //   PatientFactory.abi,PatientDeploy && PatientDeploy.address
    // );
    this.setState({
      accounts
    },this.runExample)
}catch(err){
  alert(
    `Failed to load web3, accounts, or contract. Check console for details.`,
  );
  console.log("error to doko");
  console.error(err);
}
this.medicalContract.deployed().then(function(instance){
  let patientdt = instance;
  let patientdt2=instance;
