    app.setState({
      patientData
    });
    console.log("hahaha" + data);
  },100);

  patientdt.PatientToSick(id).then(function(_idSickCode){
    patientdt2.viewSickCode(_idSickCode).then(function(data1){
      let idpatient1 = id;
      console.log("day la so " + typeof(0) + typeof(idpatient1));
      const sickCodes = [...app.state.sickCodes,{idpatient : idpatient1,data: data1}];
      app.setState({
        sickCodes
      });
      // console.log("ban ghi sick code " + data1)

    })

  })


}
  })

  // instance.countSickCode().then(function(counts){
