  runExample= async()=>{
    // const {patientContract}=this.state;
    var userAccount =this.web3.eth.accounts[0];
    // console.log(userAccount.address)
    this.medicalContract.deployed().then((instance)=>{
      let that=this;
      this.medicalContractDeployed=instance;
    this.medicalContractDeployed.CreatePatient("cuong",5453,23,1,{from:userAccount}).then((response,error)=>{
      if(response){
        let that = this;
        console.log("den day chua");
        // console.log("dung " + response);
        that.medicalContractDeployed.OwnerPatientCount(userAccount).then((data)=>{

            console.log(data);
            console.log(that.state.patientData);
            console.log("code " + that.state.sickCodes[1].data);
            console.log("code  sick " + that.state.sickCodeByPatient);

        });
        // that.setState({patientData: that.patientContractDeployed.patients(2)});

 }
      else{
        console.log("error cmnr ");
        // console.log("error" + error);
      }
    })
  })
}

