  displayPatient=(patient)=>{
    return(
      <ul>
          <li>Name: {patient.name}</li>
         <li>DNA: {patient.age}</li>
          <li>Level: {patient.cmt}</li>
           <li>Wins: {patient.sex}</li>
        </ul>)

  }

  updatePatient=()=>{
    console.log("con me no")
    if(this.state.patientData){
      let patient=this.state.patientData;
      return(
        <ul>
          <li>Name: {patient.name}</li>
         <li>DNA: {patient.age}</li>
          <li>Level: {patient.cmt}</li>
           <li>Wins: {patient.sex}</li>
        </ul>
      )
    }
  }

  // getSickbypatient=(patientID)=>{
  //   let app = this;
  //   this.medicalContract.deployed().then((instance)=>{
  //     let patientdt2 = instance;
  //   patientdt2.viewSickCode(patientID).then(function(data1){
  //     app.setState({
  //       sickCodeByPatient:data1
  //     });
  //     console.log("ban ghi sick code " + data1)

  //   })
  // })
  // }
  render() {
    if (this.isWeb3) {
      if (this.isWeb3Locked) {
        return (
          <div>
             <Header network={ this.state.network } appName={ this.appName }/>
             <SideBar  patientData ={this.state.patientData}/>
             <Content  message="Unlock Your Metamask/Mist Wallet"/>

          </div>)
      } else {
        return (
          <div>
             <Header  network={ this.state.network }   appName={ this.appName }/>
             <SideBar  patientData ={this.state.patientData}/>
             <Content getSickbypatient= {this.getSickbypatient} patientData ={this.state.patientData}/>        
            </div>)
      }
    } else {
      return (
        <div className="App">
        <Header />
        </div>
      );
    }
  }
}

export default App;
