'use strict';

var series = require('async/series');

var Bitswap = require('ipfs-bitswap');

var setImmediate = require('async/setImmediate');

var promisify = require('promisify-es6');

var IPNS = require('../ipns');

var routingConfig = require('../ipns/routing/config');

var createLibp2pBundle = require('./libp2p');

module.exports = function (self) {
  return promisify(function (callback) {
    var done = function done(err) {
      if (err) {
        setImmediate(function () {
          return self.emit('error', err);
        });
        return callback(err);
      }

      self.state.started();
      setImmediate(function () {
        return self.emit('start');
      });
      callback();
    };

    if (self.state.state() !== 'stopped') {
      return done(new Error("Not able to start from state: ".concat(self.state.state())));
    }

    self.log('starting');
    self.state.start();
    series([function (cb) {
      // The repo may be closed if previously stopped
      self._repo.closed ? self._repo.open(cb) : cb();
    }, function (cb) {
      self._repo.config.get(function (err, config) {
        if (err) return cb(err);
        var libp2p = createLibp2pBundle(self, config);
        libp2p.start(function (err) {
          if (err) return cb(err);
          self.libp2p = libp2p;
          cb();
        });
      });
    }, function (cb) {
      var ipnsRouting = routingConfig(self);
      self._ipns = new IPNS(ipnsRouting, self._repo.datastore, self._peerInfo, self._keychain, self._options);
      self._bitswap = new Bitswap(self.libp2p, self._repo.blocks, {
        statsEnabled: true
      });

      self._bitswap.start();

      self._blockService.setExchange(self._bitswap);

      self._preload.start();

      self._ipns.republisher.start();

      self._mfsPreload.start(cb);
    }], done);
  });
};
