import React, { Component } from 'react';
import ipfs from './ipfs';

class Patient extends Component {
  constructor(props) {
    super();
    this.state={
      ipfsHashData:"",
      ipfsHashImg:""
    }

  }

 loadFile=()=> {
    var input, file, fr;
    console.log("denday chua");

    input = document.getElementById('fileinput');

    file = input.files[0];
    fr = new FileReader();
    fr.onload = receivedText();


    function receivedText() {
        fr = new FileReader();
        fr.readAsBinaryString(file);

        //IPFS START
            const files = [
            {
                path: 'image.png',
                content: ipfs.types.Buffer.from(btoa(fr.result),"base64")
            }
            ]

            ipfs.files.add(files, function (err, files) {
                let url = "https://ipfs.io/ipfs/"+files[0].hash;
                console.log("Storing file on IPFS using Javascript. HASH: https://ipfs.io/ipfs/"+files[0].hash);
                let ipfsPath = files[0].hash;
                console.log("hash of img :" + ipfsPath);
                this.setState({
                  ipfsHashImg:ipfsPath
                })
            })

            // const log = (line) => {
            //     document.getElementById('output').appendChild(document.createTextNode(`${line}\r\n`))
            // }



    }


}


 loadImage = (input)=> {
    var input, file, fr;




        ipfs.on('ready', () => {

                let ipfsPath = input
                ipfs.files.cat(ipfsPath, function (err, file) {
                if (err) {
                    throw err
                }
               let  img = file.toString("base64");
                document.getElementById("design_display").src= "data:image/png;base64," + img;
                })

            const log = (line) => {
                document.getElementById('output').appendChild(document.createTextNode(`${line}\r\n`))
            }


        })



}
  ipfsUpload = async () => {
    const buffer = await Buffer.from("toi di an thit cho");
    ipfs.add(buffer, (err, ipfsHash) => {
      if (err) return console.error(err)
      console.log( "ma ban" + ipfsHash[0].hash)
      this.setState({
        ipfsHashData: ipfsHash[0].hash
      })

    })
  }
  ipfsGetdata = async () => {
    ipfs.cat(this.state.ipfsHashData, (err, data) => {
      if (err) return console.error(err)
      console.log( "data" + data.toString())
    })
  }
  render() {
    return (
      <div className="col-md-3">
        <a href="#">
          <span>Create medical </span>
          <span className="pull-right-container">
          </span>
        </a>
        <input type="text"  ></input>
        <input type="submit" onClick={event => this.ipfsUpload()}></input>
        <input type="submit"  value="get data" onClick={event => this.ipfsGetdata()}></input>
        <input type="file" id = "fileinput" ></input>
        <input type="submit" value="submit file" onClick={event=>this.loadFile()}></input>
        <input type="submit" value="show file" onClick={event=>this.loadImage(this.state.ipfsHashImg)}></input>
        <img  id = "design_display"/>

      </div>
    )
  }
}

export default Patient;
