import React, { Component } from 'react';
import ipfs from './ipfs';

class Patient extends Component {
  constructor(props) {
    super();

  }

  ipfsUpload = async() => {
    const buffer = await Buffer.from("toi di an thit cho");
   ipfs.add(buffer).then(function(err, ipfsHash){
    console.log(err, ipfsHash);
   }) 
  }
  render() {
    return (
      <div className="col-md-3">
        <a href="#">
          <span>Create medical </span>
          <span className="pull-right-container">
          </span>
        </a>
        <input type="text"  ></input>
        <input type="submit" onClick={this.ipfsUpload()}></input>
      </div>
    )
  }
}

export default Patient;
