import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Web3 from 'web3';
import TruffleContract from 'truffle-contract';
import Nav from "./Components/Nav";
import UnlockMetamask from "./Components/UnlockMetamask";
import PatientFactory from './build/contracts/PatientFactory.json';
import SetSick from "./build/contracts/SetSick.json";
import SickFactory from './build/contracts/SickFactory.json';
import Container from './Components/Container';


class App extends Component {
  constructor() {
    super();
    this.isWeb3 = true;
    this.isWeb3Locked = false;
    this.appName = "Patient Data";

    this.state = {
      network: "checking...",
      account: null
    };

    let web3 = window.web3;
    if (typeof web3 !== "undefined") {
      this.web3Provider = web3.currentProvider;
      this.web3 = new Web3(web3.currentProvider);

      if (web3.eth.coinbase === null)
        this.isWeb3Locked = true;
    } else {

      this.isWeb3 = false;
    }
  }

  setNetwork = () => {
    let networkName,
      that = this;

    this.web3.version.getNetwork(function(err, networkId) {
      switch (networkId) {
      case "1":
        networkName = "Main";
        break;
      case "2":
        networkName = "Morden";
        break;
      case "3":
        networkName = "Ropsten";
        break;
      case "4":
        networkName = "Rinkeby";
        break;
      case "42":
        networkName = "Kovan";
        break;
      default:
        networkName = networkId;
      }
      that.setState({
        network: networkName
      })
    });
  };

  getPatient = ()=>{
    }


  componentDidMount = async() => {
    let account = this.web3.eth.coinbase;
    let app = this;
try{
    this.setNetwork(); 

    const accounts = await this.web3.eth.getAccounts();
    const PatientDeploy= PatientFactory.networks[this.state.network];
    const SickDeploy=SickFactory.networks[this.state.network];
    const SetSickDeploy=SetSick.networks[this.state.network];

    const patientContract =this.web3.eth.contract(
      PatientFactory.abi,PatientDeploy && PatientDeploy.address
    );

    this.setState({
      patientContract:patientContract,
      accounts:accounts
    })
}catch(err){
  alert(
    `Failed to load web3, accounts, or contract. Check console for details.`,
  );
  console.error(err);
}
  }

  setPatient = (name,cmt,age,sex)=>{
    this.state.patientContract.methods._CreatePatient(name,cmt,age,sex).send({from:accounts[0]})

  }


  render() {
    if (this.isWeb3) {
      if (this.isWeb3Locked) {
        return (
          <div>
            <Nav appName={ this.appName } network={ this.state.network } />
            <UnlockMetamask message="Unlock Your Metamask/Mist Wallet" />
          </div>)
      } else {
        return (
          <div>
            <Nav appName={ this.appName } network={ this.state.network } />
            <Container/>
            <h2> den day roi </h2></div>)
      }
    } else {
      return (
        <div className="App">
          <header className="App-header">
            <img src={ logo } className="App-logo" alt="logo" />
            <p>
              Edit <code>src/App.js</code> and save to reload.
            </p>
            <a className="App-link" href="https://reactjs.org" target="_blank" rel="noopener noreferrer">
                                                                                       start up
                                                                                     </a>
          </header>
        </div>
      );
    }
  }
}

export default App;
