import React, { Component } from 'react';
import Modal from 'react-awesome-modal';
import RecordMedicalDetail from './RecordMedicalDetail';


class RecordMedical extends Component {
  constructor(props) {
    super();
    // this.handleShow = this.handleShow.bind(this);
    // this.handleClose = this.handleClose.bind(this);
    this.state = {
      visible: false,
    };
  }

  closeModal() {
    this.setState({ visible: false });
  }

  openModal() {
    this.setState({ visible: true });
  }

  render() {
    return (
      <div className="col-md-3">
        <a href="#">
          <span>Record by </span>
          <span className="pull-right-container">
          </span>
        </a>
        <ul>
          {
            this.props.recordMedical.map((data) =>

              <li className="input2">


                <section>
                  <input type="button" value={data.realTime} onClick={() => this.openModal()} />
                  <Modal visible={this.state.visible} width="400" height="300" effect="fadeInUp" onClickAway={() => this.closeModal()}>
                    <div>
                      <RecordMedicalDetail dataRecordDetail={data.description}  realTime ={data.realTime}/>
                      <a href="javascript:void(0);" onClick={() => this.closeModal()}>Close</a>
                    </div>
                  </Modal>
                </section>
              </li>

            )
          }

        </ul>

      </div>
    )
  }
}

export default RecordMedical;
