'use strict';

var multihashes = require('multihashes');

var CID = require('cids');

var protobuf = require('protons');

var fnv1a = require('fnv1a');

var varint = require('varint');

var _require = require('ipld-dag-pb'),
    DAGNode = _require.DAGNode,
    DAGLink = _require.DAGLink;

var multicodec = require('multicodec');

var someSeries = require('async/someSeries');

var eachOfSeries = require('async/eachOfSeries');

var pbSchema = require('./pin.proto');

var emptyKeyHash = 'QmdfTbBqBPQ7VNxZEYEj14VmRuZBkqFbiwReogJgS1zR1n';
var emptyKey = multihashes.fromB58String(emptyKeyHash);
var defaultFanout = 256;
var maxItems = 8192;
var pb = protobuf(pbSchema);

function toB58String(hash) {
  return new CID(hash).toBaseEncodedString();
}

function readHeader(rootNode) {
  // rootNode.data should be a buffer of the format:
  // < varint(headerLength) | header | itemData... >
  var rootData = rootNode.Data;
  var hdrLength = varint.decode(rootData);
  var vBytes = varint.decode.bytes;

  if (vBytes <= 0) {
    throw new Error('Invalid Set header length');
  }

  if (vBytes + hdrLength > rootData.length) {
    throw new Error('Impossibly large set header length');
  }

  var hdrSlice = rootData.slice(vBytes, hdrLength + vBytes);
  var header = pb.Set.decode(hdrSlice);

  if (header.version !== 1) {
    throw new Error("Unsupported Set version: ".concat(header.version));
  }

  if (header.fanout > rootNode.Links.length) {
    throw new Error('Impossibly large fanout');
  }

  return {
    header: header,
    data: rootData.slice(hdrLength + vBytes)
  };
}

function hash(seed, key) {
  var buf = Buffer.alloc(4);
  buf.writeUInt32LE(seed, 0);
  var data = Buffer.concat([buf, Buffer.from(toB58String(key))]);
  return fnv1a(data.toString('binary'));
}

exports = module.exports = function (dag) {
  var pinSet = {
    // should this be part of `object` API?
    hasDescendant: function hasDescendant(root, childhash, callback) {
      var seen = {};

      if (CID.isCID(childhash) || Buffer.isBuffer(childhash)) {
        childhash = toB58String(childhash);
      }

      return searchChildren(root, callback);

      function searchChildren(root, cb) {
        someSeries(root.Links, function (link, done) {
          var cid = link.Hash;
          var bs58Link = toB58String(cid);

          if (bs58Link === childhash) {
            return done(null, true);
          }

          if (bs58Link in seen) {
            return done(null, false);
          }

          seen[bs58Link] = true;
          dag.get(cid, '', {
            preload: false
          }, function (err, res) {
            if (err) {
              return done(err);
            }

            searchChildren(res.value, done);
          });
        }, cb);
      }
    },
    storeSet: function storeSet(keys, callback) {
      var pins = keys.map(function (key) {
        if (typeof key === 'string' || Buffer.isBuffer(key)) {
          key = new CID(key);
        }

        return {
          key: key,
          data: null
        };
      });
      pinSet.storeItems(pins, function (err, rootNode) {
        if (err) {
          return callback(err);
        }

        dag.put(rootNode, {
          version: 0,
          format: multicodec.DAG_PB,
          hashAlg: multicodec.SHA2_256,
          preload: false
        }, function (err, cid) {
          if (err) {
            return callback(err, cid);
          }

          callback(null, {
            node: rootNode,
            cid: cid
          });
        });
      });
    },
    storeItems: function storeItems(items, callback) {
      return storePins(items, 0, callback);

      function storePins(pins, depth, storePinsCb) {
        var pbHeader = pb.Set.encode({
          version: 1,
          fanout: defaultFanout,
          seed: depth
        });
        var headerBuf = Buffer.concat([Buffer.from(varint.encode(pbHeader.length)), pbHeader]);
        var fanoutLinks = [];

        for (var i = 0; i < defaultFanout; i++) {
          fanoutLinks.push(new DAGLink('', 1, emptyKey));
        }

        if (pins.length <= maxItems) {
          var nodes = pins.map(function (item) {
            return {
              link: new DAGLink('', 1, item.key),
              data: item.data || Buffer.alloc(0)
            };
          }) // sorting makes any ordering of `pins` produce the same DAGNode
          .sort(function (a, b) {
            return Buffer.compare(a.link.Hash.buffer, b.link.Hash.buffer);
          });
          var rootLinks = fanoutLinks.concat(nodes.map(function (item) {
            return item.link;
          }));
          var rootData = Buffer.concat([headerBuf].concat(nodes.map(function (item) {
            return item.data;
          })));
          var rootNode;

          try {
            rootNode = DAGNode.create(rootData, rootLinks);
          } catch (err) {
            return storePinsCb(err);
          }

          return storePinsCb(null, rootNode);
        } else {
          // If the array of pins is > maxItems, we:
          //  - distribute the pins among `defaultFanout` bins
          //    - create a DAGNode for each bin
          //      - add each pin as a DAGLink to that bin
          //  - create a root DAGNode
          //    - add each bin as a DAGLink
          //  - send that root DAGNode via callback
          // (using go-ipfs' "wasteful but simple" approach for consistency)
          // https://github.com/ipfs/go-ipfs/blob/master/pin/set.go#L57
          var bins = pins.reduce(function (bins, pin) {
            var n = hash(depth, pin.key) % defaultFanout;
            bins[n] = n in bins ? bins[n].concat([pin]) : [pin];
            return bins;
          }, {});
          eachOfSeries(bins, function (bin, idx, eachCb) {
            storePins(bin, depth + 1, function (err, child) {
              return storeChild(err, child, idx, eachCb);
            });
          }, function (err) {
            if (err) {
              return storePinsCb(err);
            }

            var rootNode;

            try {
              rootNode = DAGNode.create(headerBuf, fanoutLinks);
            } catch (err) {
              return storePinsCb(err);
            }

            return storePinsCb(null, rootNode);
          });
        }

        function storeChild(err, child, binIdx, cb) {
          if (err) {
            return cb(err);
          }

          var opts = {
            version: 0,
            format: multicodec.DAG_PB,
            hashAlg: multicodec.SHA2_256,
            preload: false
          };
          dag.put(child, opts, function (err, cid) {
            if (err) {
              return cb(err);
            }

            fanoutLinks[binIdx] = new DAGLink('', child.size, cid);
            cb(null);
          });
        }
      }
    },
    loadSet: function loadSet(rootNode, name, callback) {
      var link = rootNode.Links.find(function (l) {
        return l.Name === name;
      });

      if (!link) {
        return callback(new Error('No link found with name ' + name));
      }

      dag.get(link.Hash, '', {
        preload: false
      }, function (err, res) {
        if (err) {
          return callback(err);
        }

        var keys = [];

        var step = function step(link) {
          return keys.push(link.Hash.buffer);
        };

        pinSet.walkItems(res.value, step, function (err) {
          if (err) {
            return callback(err);
          }

          return callback(null, keys);
        });
      });
    },
    walkItems: function walkItems(node, step, callback) {
      var pbh;

      try {
        pbh = readHeader(node);
      } catch (err) {
        return callback(err);
      }

      eachOfSeries(node.Links, function (link, idx, eachCb) {
        if (idx < pbh.header.fanout) {
          // the first pbh.header.fanout links are fanout bins
          // if a fanout bin is not 'empty', dig into and walk its DAGLinks
          var linkHash = link.Hash.buffer;

          if (!emptyKey.equals(linkHash)) {
            // walk the links of this fanout bin
            return dag.get(linkHash, '', {
              preload: false
            }, function (err, res) {
              if (err) {
                return eachCb(err);
              }

              pinSet.walkItems(res.value, step, eachCb);
            });
          }
        } else {
          // otherwise, the link is a pin
          step(link, idx, pbh.data);
        }

        eachCb(null);
      }, callback);
    }
  };
  return pinSet;
};
