import React, { Component } from 'react';
import Patient from './Patient';
import Sick from './Sick';
import RecordMedical from './RecordMedical';

export default class ContentSendPatient extends Component {
constructor(props){
    super();
    this.state = {
      idPatient:null

    }

}
handlSetPatientTransport=(id)=>{

  this.setState({
    idPatient:id[0]
  });
  this.props.setPatientTransport(id);

}

patientDetal=()=>{
  let app = this;
  this.props.patientData.map((data)=>{
if(data[0].c[0]==app.state.idPatient){
  return(<ul>
    <li>NAME:{data[1]}</li>
    <li>AGE:{data[2].c}</li>

  </ul>)
}
  })
}


  render() {

    return (
      <div className=" contentlayout content-wrapper">
        <section className="content-header">
          <div className="row">
            <div className="col-md-3">
              <div className="box">
              <a href="#">
                                <span>Patient Name</span>
                                <span className="pull-right-container">
                                </span>
                            </a>
        <ul>
          {
              this.props.patientData.map((data)=>

          <li  className="input2">
          <a onClick= {event => this.props.handlSetPatientTransport(data[0].c)} >{data[1]}</a>
              </li>

              )
          }

        </ul>

              </div>
            </div>
            <div className="col-md-3">
            {this.patientDetal}


            </div>
          </div>
        </section>
      </div>
    )
  }
}
