'use strict';

var OFFLINE_ERROR = require('../utils').OFFLINE_ERROR;

var promisify = require('promisify-es6');

var setImmediate = require('async/setImmediate');

var Big = require('bignumber.js');

var CID = require('cids');

var PeerId = require('peer-id');

var errCode = require('err-code');

function formatWantlist(list, cidBase) {
  return Array.from(list).map(function (e) {
    return {
      '/': e[1].cid.toBaseEncodedString(cidBase)
    };
  });
}

module.exports = function bitswap(self) {
  return {
    wantlist: promisify(function (peerId, callback) {
      if (typeof peerId === 'function') {
        callback = peerId;
        peerId = null;
      }

      if (!self.isOnline()) {
        return setImmediate(function () {
          return callback(new Error(OFFLINE_ERROR));
        });
      }

      var list;

      if (peerId) {
        try {
          peerId = PeerId.createFromB58String(peerId);
        } catch (e) {
          peerId = null;
        }

        if (!peerId) {
          return setImmediate(function () {
            return callback(new Error('Invalid peerId'));
          });
        }

        list = self._bitswap.wantlistForPeer(peerId);
      } else {
        list = self._bitswap.getWantlist();
      }

      setImmediate(function () {
        return callback(null, {
          Keys: formatWantlist(list)
        });
      });
    }),
    stat: promisify(function (callback) {
      if (!self.isOnline()) {
        return setImmediate(function () {
          return callback(new Error(OFFLINE_ERROR));
        });
      }

      var snapshot = self._bitswap.stat().snapshot;

      setImmediate(function () {
        callback(null, {
          provideBufLen: parseInt(snapshot.providesBufferLength.toString()),
          blocksReceived: new Big(snapshot.blocksReceived),
          wantlist: formatWantlist(self._bitswap.getWantlist()),
          peers: self._bitswap.peers().map(function (id) {
            return id.toB58String();
          }),
          dupBlksReceived: new Big(snapshot.dupBlksReceived),
          dupDataReceived: new Big(snapshot.dupDataReceived),
          dataReceived: new Big(snapshot.dataReceived),
          blocksSent: new Big(snapshot.blocksSent),
          dataSent: new Big(snapshot.dataSent)
        });
      });
    }),
    unwant: promisify(function (keys, callback) {
      if (!self.isOnline()) {
        return setImmediate(function () {
          return callback(new Error(OFFLINE_ERROR));
        });
      }

      if (!Array.isArray(keys)) {
        keys = [keys];
      }

      try {
        keys = keys.map(function (key) {
          if (CID.isCID(key)) {
            return key;
          }

          return new CID(key);
        });
      } catch (err) {
        return setImmediate(function () {
          return callback(errCode(err, 'ERR_INVALID_CID'));
        });
      }

      setImmediate(function () {
        return callback(null, self._bitswap.unwant(keys));
      });
    })
  };
};
