import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Web3 from 'web3';
import TruffleContract from 'truffle-contract';
import Nav from "./Components/Nav";
import UnlockMetamask from "./Components/UnlockMetamask";


class App extends Component {
  constructor() {
    super();
    this.isWeb3 = true;
    this.isWeb3Locked = false;

    this.state = {
      network: "checking...",
      account: null,
      appName: "patientData"
    };

    let web3 = window.web3;
    if (typeof web3 !== "undefined") {
      this.web3Provider = web3.currentProvider;
      this.web3 = new Web3(web3.currentProvider);
      if (web3.eth.coinbase === null)
        this.isWeb3Locked = true;
    } else {
      this.isWeb3 = false;
    }
  }
  setNetwork = () => {
    let networkName,
      that = this;

    this.web3.version.getNetwork(function(err, networkId) {
      switch (networkId) {
      case "1":
        networkName = "Main";
        break;
      case "2":
        networkName = "Morden";
        break;
      case "3":
        networkName = "Ropsten";
        break;
      case "4":
        networkName = "Rinkeby";
        break;
      case "42":
        networkName = "Kovan";
        break;
      default:
        networkName = networkId;
      }
      that.setState({
        network: networkName
      })
    });
  };


  componentDidMount() {
    let account = this.web3.eth.coinbase;
    let app = this;

    this.setNetwork();
  }


  render() {
    if (this.isWeb3) {
      if (this.isWeb3Locked) {
        return (
          <div>
            <Nav appName={ this.appName } network={ this.state.network } />
            <UnlockMetamask message="Unlock Your Metamask/Mist Wallet" />
          </div>)
      }
    } else {
      return (
        <div className="App">
          <header className="App-header">
            <img src={ logo } className="App-logo" alt="logo" />
            <p>
              Edit <code>src/App.js</code> and save to reload.
            </p>
            <a className="App-link" href="https://reactjs.org" target="_blank" rel="noopener noreferrer">
                                                             start up
                                                                </a>
          </header>
        </div>
      );
    }
  }
}

export default App;
