'use strict';

var promisify = require('promisify-es6');

var pull = require('pull-stream');

module.exports = function (self) {
  return promisify(function (ipfsPath, options, callback) {
    if (typeof options === 'function') {
      callback = options;
      options = {};
    }

    options = options || {};
    pull(self.getPullStream(ipfsPath, options), pull.asyncMap(function (file, cb) {
      if (file.content) {
        pull(file.content, pull.collect(function (err, buffers) {
          if (err) {
            return cb(err);
          }

          file.content = Buffer.concat(buffers);
          cb(null, file);
        }));
      } else {
        cb(null, file);
      }
    }), pull.collect(callback));
  });
};
