import Container from './Components/Container';
import ReactDOM from 'react-dom';
import Header from './Components/Header';
import 'react-sticky-header/styles.css';
import SideBar from './Components/SideBar';


class App extends Component {
  constructor() {
    super();
    this.isWeb3 = true;
    this.isWeb3Locked = false;
    this.appName = "Patient Data";

    this.state = {
      network: "checking...",
      accounts: null,
      medicalContract:null,
      patientData:[]
    };

    let web3 = window.web3;
    if (typeof web3 !== "undefined") {
      this.web3Provider = web3.currentProvider;
      this.web3 = new Web3(web3.currentProvider);
      this.medicalContract = TruffleContract(Medical);
      this.medicalContract.setProvider(this.web3Provider);

      if (web3.eth.coinbase === null)
        this.isWeb3Locked = true;
    } else {

      this.isWeb3 = false;
    }
  }

  setNetwork = () => {
    let networkName,
      that = this;

    this.web3.version.getNetwork(function(err, networkId) {
      switch (networkId) {
      case "1":
        networkName = "Main";
        break;
      case "2":
        networkName = "Morden";
        break;
      case "3":
        networkName = "Ropsten";
        break;
      case "4":
        networkName = "Rinkeby";
        break;
      case "42":
        networkName = "Kovan";
        break;
      default:
        networkName = networkId;
      }
      that.setState({
        network: networkName
      })
    });
  };

