'use strict';

var utils = require('../../utils');

var errcode = require('err-code');

module.exports = function (dht) {
  var log = utils.logger(dht.peerInfo.id, 'rpc:put-value');
  /**
   * Process `PutValue` DHT messages.
   *
   * @param {PeerInfo} peer
   * @param {Message} msg
   * @param {function(Error, Message)} callback
   * @returns {undefined}
   */

  return function putValue(peer, msg, callback) {
    var key = msg.key;
    log('key: %b', key);
    var record = msg.record;

    if (!record) {
      var errMsg = "Empty record from: ".concat(peer.id.toB58String());
      log.error(errMsg);
      return callback(errcode(new Error(errMsg), 'ERR_EMPTY_RECORD'));
    }

    dht._verifyRecordLocally(record, function (err) {
      if (err) {
        log.error(err.message);
        return callback(err);
      }

      record.timeReceived = new Date();
      var key = utils.bufferToKey(record.key);
      dht.datastore.put(key, record.serialize(), function (err) {
        if (err) {
          return callback(err);
        }

        callback(null, msg);
      });
    });
  };
};
