import React, { Component } from 'react';
import Patient from './Patient';
import Sick from './Sick';
import RecordMedical from './RecordMedical';

export default class ContentSendPatient extends Component {
constructor(props){
    super();
    this.state = {
      PatientDetail:[0,0,0,0]

    }

}
handlSetPatientTransport=(id)=>{
  this.props.patientData.map((data)=>{
    if(data[0].c==id){
      this.setState({
        PatientDetail:data
      })
      console.log("data" + data);
    }
  })

  this.props.setPatientTransport(id);

  console.log(id + "show ra rooi" + this.state.PatientDetail)

}



  render() {

    return (
      <div className=" contentlayout content-wrapper">
        <section className="content-header">
          <div className="row">
            <div className="col-md-3">
              <div className="box">
              <a href="#">
                                <span>Patient Name</span>
                                <span className="pull-right-container">
                                </span>
                            </a>
        <ul>
          {
              this.props.patientData.map((data)=>

          <li  className="input2">
          <a onClick= {event => this.handlSetPatientTransport(data[0].c)} >{data[1]}</a>
              </li>

              )
          }

        </ul>

              </div>
            </div>
            <div className="col-md-3">
            <ul>
              <li>Name:{this.state.PatientDetail[1]}</li>
              <li>Age:{this.state.PatientDetail[2]}</li>
              <li>ame:{this.state.PatientDetail[3]}</li>
            </ul>



            </div>
          </div>
        </section>
      </div>
    )
  }
}
